<?php

namespace Packagist\WebBundle\Repository;

use Packagist\WebBundle\Entity\Package;
use Packagist\WebBundle\Entity\User;

/**
 * GruopRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class GroupRepository extends \Doctrine\ORM\EntityRepository
{
    /**
     * @param User $user
     * @param Package $package
     *
     * @return array
     */
    public function getAllowedVersionByPackage(User $user, Package $package)
    {
        $qb = $this->_em->createQueryBuilder();
        $qb
            ->select('acl.version')
            ->distinct(true)
            ->from('PackagistWebBundle:User', 'u')
            ->innerJoin('u.groups', 'g')
            ->innerJoin('g.aclPermissions', 'acl')
            ->innerJoin('acl.package', 'p')
            ->where($qb->expr()->eq('u.id', $user->getId()))
            ->andWhere($qb->expr()->eq('p.id', $package->getId()));

        $result = $qb->getQuery()->getResult();
        if ($result) {
            $result = \array_column($result, 'version');
        }

        return $result;
    }

    /**
     * @param User $user
     * @param bool $hydration
     *
     * @return Package[]
     */
    public function getAllowedPackagesForUser(User $user, $hydration = true)
    {
        $qb = $this->_em->createQueryBuilder();
        $qb
            ->select('p.id')
            ->distinct(true)
            ->from('PackagistWebBundle:User', 'u')
            ->innerJoin('u.groups', 'g')
            ->innerJoin('g.aclPermissions', 'acl')
            ->innerJoin('acl.package', 'p')
            ->where($qb->expr()->eq('u.id', $user->getId()));

        $result = $qb->getQuery()->getResult();
        if ($hydration && $result) {
            $qb = $this->_em->createQueryBuilder();
            $qb->select('p')
                ->from('PackagistWebBundle:Package', 'p')
                ->where('p.id IN (:ids)')
                ->setParameter('ids', array_column($result, 'id'));

            return $qb->getQuery()->getResult();
        }

        return $result ? array_column($result, 'id') : [];
    }
}
